import React, { useState } from "react";
import "./App.css";

function App() {
  const [tasks, setTasks] = useState([
    { id: 1, text: "Read recommended book again", completed: true },
    { id: 2, text: "Vacation planning", completed: false },
    { id: 3, text: "Cook dinner", completed: false },
    { id: 4, text: "Sign up for training", completed: false },
  ]);
  const [searchText, setSearchText] = useState("");
  const [filter, setFilter] = useState("all");
  const [darkMode, setDarkMode] = useState(false);
  const [showAddSection, setShowAddSection] = useState(false); // State to show the add task section
  const [newTaskText, setNewTaskText] = useState("");
  const [editingTaskId, setEditingTaskId] = useState(null); // State for editing task
  const [editedTaskText, setEditedTaskText] = useState(""); // State for edited text

  // Toggle completed status
  const handleToggleCompleted = (id) => {
    setTasks(
      tasks.map((task) =>
        task.id === id ? { ...task, completed: !task.completed } : task
      )
    );
  };

  // Add a new task
  const handleAddTask = () => {
    if (newTaskText.trim()) {
      const newTask = {
        id: tasks.length + 1,
        text: newTaskText,
        completed: false,
      };
      setTasks([...tasks, newTask]);
      setNewTaskText(""); // Clear input after adding
      setShowAddSection(false); // Hide add section after adding
    }
  };

  // Edit a task
  const handleEditTask = (id) => {
    const taskToEdit = tasks.find((task) => task.id === id);
    setEditingTaskId(id);
    setEditedTaskText(taskToEdit.text);
  };

  // Save edited task
  const handleSaveEdit = (id) => {
    setTasks(
      tasks.map((task) =>
        task.id === id ? { ...task, text: editedTaskText } : task
      )
    );
    setEditingTaskId(null); // Exit edit mode
    setEditedTaskText(""); // Clear edited text state
  };

  // Delete a task
  const handleDeleteTask = (id) => {
    setTasks(tasks.filter((task) => task.id !== id));
  };

  const filteredTasks = tasks
    .filter((task) => {
      if (filter === "completed") return task.completed;
      if (filter === "uncompleted") return !task.completed;
      return true;
    })
    .filter((task) => task.text.toLowerCase().includes(searchText.toLowerCase()));

  return (
    <div className={`todo-app ${darkMode ? "dark-mode" : ""}`}>
      <div className="todo-header">
        <h1>TODO LIST</h1>
        <input
          type="text"
          placeholder="Search note..."
          value={searchText}
          onChange={(e) => setSearchText(e.target.value)}
        />
        <div className="todo-controls">
          <select value={filter} onChange={(e) => setFilter(e.target.value)}>
            <option value="all">ALL</option>
            <option value="completed">Completed</option>
            <option value="uncompleted">Uncompleted</option>
          </select>
          <button
            className="theme-toggle-btn"
            onClick={() => setDarkMode(!darkMode)}
          >
            {darkMode ? "üåû" : "üåô"}
          </button>
        </div>
      </div>
      <div className="todo-list">
        {filteredTasks.map((task) => (
          <div
            key={task.id}
            className={`todo-item ${task.completed ? "completed" : ""}`}
          >
            <input
              type="checkbox"
              checked={task.completed}
              onChange={() => handleToggleCompleted(task.id)}
            />
            {editingTaskId === task.id ? (
              <>
                <input
                  type="text"
                  value={editedTaskText}
                  onChange={(e) => setEditedTaskText(e.target.value)}
                />
                <button onClick={() => handleSaveEdit(task.id)}>Save</button>
              </>
            ) : (
              <>
                <p>{task.text}</p>
                <button className="edit-btn" onClick={() => handleEditTask(task.id)}>
                  ‚úèÔ∏è
                </button>
                <button className="delete-btn" onClick={() => handleDeleteTask(task.id)}>
                  üóëÔ∏è
                </button>
              </>
            )}
          </div>
        ))}
      </div>

      {showAddSection && (
        <div className="add-task-section">
          <input
            type="text"
            placeholder="New task..."
            value={newTaskText}
            onChange={(e) => setNewTaskText(e.target.value)}
          />
          <button className="add-task-btn" onClick={handleAddTask}>Add</button>
        </div>
      )}

      <button className="add-btn" onClick={() => setShowAddSection(!showAddSection)}>
        +
      </button>
    </div>
  );
}

export default App;
